import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import ThumbUpIcon from '@material-ui/icons/ThumbUp';
import ThumbDownIcon from '@material-ui/icons/ThumbDown';

const UrlAPI = 'http://localhost:3000/api';

// STYLE :

const useStyles = makeStyles((theme) => ({
    mentionButton: {
        display: 'flex',
        alignContent: 'center',
        backgroundColor: '#D64F30',
        color: 'white',
        height: '5ch',
        marginRight: '1ch',
        marginTop: '1ch',
        padding: '1ch',
        "&:hover": {
            cursor: 'pointer',
            backgroundColor: '#FF5F39',
        },
    },
    mentionNumber: {
        fontWeight: '600',
    },
}));

// LOGIQUE :

const Likes = ({postId}) => {

    const classes = useStyles();

    const [mentions, setMentions] = useState({});

    let currentUser = JSON.parse(sessionStorage.getItem("currentUser"));

    useEffect ( () => {
        axios.get(UrlAPI + '/mentions/' + postId)
        .then(res => setMentions(res.data))
        .catch(err => console.log(err));
    }, []);

    const handleLike = () => {
        axios({
            method: 'post',
            url: UrlAPI + '/mentions/' + postId,
            data: {user: currentUser.id, post: postId, mention: 1}
        })
        .then(result => console.log("result here !", result))
        .catch(err => console.log(err));
        return;
    };


    return(
        <>
            <div className={classes.mentionsBlock}>
                <div className={classes.mentionButton}>
                    <ThumbUpIcon onClick={handleLike} />
                    <span className={classes.mentionNumber}>{mentions && mentions.likes}</span>
                </div>
                <div className={classes.mentionButton}>
                    <ThumbDownIcon/>
                    <span className={classes.mentionNumber}>{mentions && mentions.dislikes}</span>
                </div>
            </div>
        </>
    );
};

export default Likes;
