import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import { InputLabel, MenuItem, Select, FormControl } from '@material-ui/core';

// STYLE :

const useStyles = makeStyles((theme) => ({
    createPostBlock: {
            display: 'flex',
            flexDirection: 'column',
            width: '100ch',
            margin: 'auto',  
        },
        createPostHeader: {
            backgroundColor: '#D75030',
            color: 'white',
            width: '30ch',
            padding: '2ch',
            textAlign: 'center',
            fontSize: '12px',
        },
        createPostBody: {
            backgroundColor: '#F2D3D4',
            padding: '2ch',
            width: '100%',
            margin: 'auto',
            marginBottom: '10ch',
        },
        createPostBodyContent: {
            backgroundColor: '#FFFFFE',
            width: '90ch',
            padding: '2ch',
            marginLeft: '3ch',
            marginRight: 'auto',
            textAlign: 'left',
        },  
        dropDownList: {
            //width: '30ch',
            //backgroundColor: '#D75030',
            '&:before': {
                borderColor: 'white',
            },
            '&:after': {
                borderColor: 'white',
            },
        },
        dropDownListLabel: {
            color: 'white',
        },
}));

// LOGIQUE :

const CreatePost = props => {

    const classes = useStyles();
    const [newPostTheme, setNewPostTheme] = useState('');
    const handleChange = (event) => {
        setNewPostTheme(event.target.value);
        console.log(newPostTheme);
    };
    return(
        <div className= { classes.createPostBlock }>
            <div className={classes.createPostHeader}>
                <h2>PUBLIER UN POST</h2>
            </div>
            <div className={classes.createPostBody} >
                <div className={classes.createPostBodyContent} >
                </div>
                <FormControl variant="outlined" >
                    <InputLabel className={classes.dropDownList} >Sélectionnez un thème...</InputLabel>
                    <Select  value={newPostTheme} onChange={handleChange}>
                        {props.themes && props.themes.map((theme) => { 
                        return (
                            <MenuItem key={theme.id} value={theme.id}>{theme.name}</MenuItem>
                        ); })}
                    </Select>
                </FormControl>
            </div>
        </div>
    );

};

export default CreatePost;
