import Post from './Post/Post';
import axios from 'axios';

const UrlAPI = 'http://localhost:3000/api/';

const useStyles = makeStyles((theme) => ({
        root: {
            width: '80%',
            position: 'relative',
            paddingTop: '10ch',
        },
}));

