// STYLE DU COMPOSANT :
const useStyles = makeStyles(() => ({
    root: {
        display: 'flex',
        position: 'fixed',
        alignItems: 'center',
        backgroundColor: '#F2D3D4',
        height: '10ch',
        width: '100%',
        justifyContent: 'space-between',
        paddingLeft: '30px',
        paddingRight: '30px',
        zIndex: '2',
    },
    logo: {
        height: '6ch',
    },
    leftPart: {
        display: 'flex',
        justifyContent: 'flex-start',
    },
    centerPart: {
        width: '50ch',
        justifyContent: 'center',
    },
    rightPart: {
        display: 'flex',
        alignItems: 'center',
    },
    avatar: {
        cursor: 'pointer',
    },
    link: {
        color: '#0E0C0C',
        fontWeight: '500',
        fontSize: '20px',
        marginLeft: '15px',
    },
}));

// LOGIQUE DU COMPOSANT :
const Header = () => {

    const classes = useStyles();

    let url = window.location.pathname;
    let userId = url.split('profile/')[1];
