import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import ThumbUpIcon from '@material-ui/icons/ThumbUp';
import ThumbDownIcon from '@material-ui/icons/ThumbDown';

const UrlAPI = 'http://localhost:3000/api';

// STYLE :

const useStyles = makeStyles((theme) => ({
    mentionsBlock: {
        display: 'flex',
    },
    mentionButton: {
        backgroundColor: '#D64F30',
        color: 'white',
        width: '10%',
        height: '5ch',
        marginRight: '1ch',
        marginTop: '1ch',
        padding: '1ch',
        "&:hover": {
            cursor: 'pointer',
            backgroundColor: '#FF5F39',
        },
    },
}));

// LOGIQUE :

const Likes = ({postId}) => {

    const classes = useStyles();

    let currentUser = sessionStorage.getItem("currentUser");

    const handleLike = () => {
        useEffect ( () => {
            axios.post(UrlAPI + '/mentions/' + postId, {user: currentUser.id, post: postId, mention: 1 })
                .then(result => console.log(result))
                .catch(err => console.log(err));
    }, []);
    };

    return(
        <>
            <div className={classes.mentionsBlock}>
                <div className={classes.mentionButton}>
                    <ThumbUpIcon onClick={handleLike} />
                    <span className={classes.numberOfLikes}></span>
                </div>
                <div className={classes.mentionButton}>
                    <ThumbDownIcon/>
                    <span className={classes.numberOfDislikes}></span>
                </div>
            </div>
        </>
    );
};

export default Likes;
