import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import {CssBaseline} from '@material-ui/core';
import Post from './Post/Post';
import Theme from './Theme/Theme';
import ThemeContainer from './ThemeContainer';
import axios from 'axios';

const UrlAPI = 'http://localhost:3000/api/';

const useStyles = makeStyles((theme) => ({
        root: {
            width: '80%',
            position: 'relative',
            paddingTop: '10ch',
        },
}));

const PostContainer = () => {

    const [postList, setPostList] = useState(null);

    const classes = useStyles();

    useEffect ( () => {
        axios.get(UrlAPI + 'posts')
        .then(result => result.data)
        .then(data => setPostList(data));
    }, []);

    let urlString = 'http://localhost:5000/accueil';
    let url = new URL(urlString);
    let params = url.searchParams(props.location.search).get('theme_id');
    //console.log(params)

    if (params) {
        let themeChosen = params.get('theme_id');
        console.log(themeChosen);
        return (
            <>
                <CssBaseline />
                <div className= { classes.root }>
                    {postList && postList.map(post => { 
                        if (post.theme = themeChosen) {
                            return <Post key={post.id} post={post} />; 
                        } else {
                            return;
                        }
                    })}
                </div>
            </>
        );
    } else {
        return (
            <>
                <CssBaseline />
                <div className= { classes.root }>
                    {postList && postList.map(post => { 
                    return <Post key={post.id} post={post} />;})}
                </div>
            </>
        );
    }


