const UrlAPI = 'http://localhost:3000/api/themes/';

//STYLE DU COMPOSANT :

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: '#FFFFFE',
        color: '#696A6B',
        fontSize: '18px',
        fontWeight: '400',
        padding: '1ch',
        maxWidth: '24ch',
        width: '14ch',
        textAlign: 'center',
        margin: '1ch',
        '&:hover': {
            backgroundColor: '#D35233',
            color: 'white',
        },
        clickedButton: {
            backgroundColor: 'red',
            color: 'white',
        },
    },
}));

// LOGIQUE DU COMPOSANT :

const Theme = (props) => {

    const [themeClicked, setThemeClicked] = useState();

    const classes = useStyles();

    return(
        <>
            <Button  className={ classes.root } onClick={ props.filter }  >
                {props.theme && props.theme.name} 
            </Button>
        </>
    );
};


export default Theme;
