import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';

// STYLE :

const useStyles = makeStyles(() => ({
    createPostBlock: {
            display: 'flex',
            flexDirection: 'column',
            width: '100ch',
            margin: 'auto',  
        },
        createPostHeader: {
            backgroundColor: '#D75030',
            color: 'white',
            width: '30ch',
            padding: '2ch',
            textAlign: 'center',
            fontSize: '12px',
        },
        createPostBody: {
            backgroundColor: '#F2D3D4',
            padding: '2ch',
            width: '100%',
            margin: 'auto',
            marginBottom: '10ch',
        },
        createPostBodyContent: {
            backgroundColor: '#FFFFFE',
            width: '90ch',
            padding: '2ch',
            marginLeft: '3ch',
            marginRight: 'auto',
            textAlign: 'left',
        },  
}));

// LOGIQUE :

const CreatePost = () => {

    const classes = useStyles();
    const [newPostTheme, setNewPostTheme] = useState();
    const handleChange = (event) => {
        setNewPostTheme(event.target.value);
        console.log(newPostTheme);
    };
    return(
        <div className= { classes.createPostBlock }>
            <div className={classes.createPostHeader}>
                <h2>PUBLIER UN POST</h2>
            </div>
            <div className={classes.createPostBody} >
                <div className={classes.createPostBodyContent} >
                </div>
                <InputLabel id="demo-simple-select-helper-label">Sélectionnez un thème...</InputLabel>
                <Select
                    labelId="demo-simple-select-helper-label"
                    id="demo-simple-select-helper"
                    value={newPostTheme}
                    onChange={handleChange}
                >
                    <MenuItem value=""><em>None</em></MenuItem>
                    <MenuItem value={10}>Ten</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                </Select>
            </div>
        </div>
    );

};

export default CreatePost;
