        return(
            <FormWrapper>
                <form className="portal-form">
                    <div className="form-content">
                        <input 
                            type="email"
                            id="email"
                            name="email"
                            //On met à jour la valeur de login avec setName() qui prend la valeur saisie comme nouvelle valeur.
                            onChange={ (e) => setEmail(e.target.value) }
