                            value={ email }
                        />
                        <input 
                            type="text"
                            id="password"
                            name="password"
                            onChange={ (e) => setPassword(e.target.value) }
                            placeholder="Mot de passe"
                            value={ password }
                        />
                        <input 
                            className="button"
