import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import { InputLabel, MenuItem, Select, FormControl } from '@material-ui/core';

// STYLE :

const useStyles = makeStyles((theme) => ({
    createPostBlock: {
            display: 'flex',
            flexDirection: 'column',
            width: '100ch',
            margin: 'auto',  
        },
        createPostHeader: {
            backgroundColor: '#D75030',
            color: 'white',
            width: '30ch',
            padding: '2ch',
            textAlign: 'center',
            fontSize: '12px',
        },
        createPostBody: {
            display: 'flex',
            flexDirection: 'column',
            backgroundColor: '#F2D3D4',
            padding: '2ch',
            width: '100%',
            margin: 'auto',
            marginBottom: '10ch',
        },
        createPostBodyContent: {
            backgroundColor: '#FFFFFE',
            width: '95%',
            margin: 'auto',
            padding: '2ch',
            textAlign: 'left',
        },  
        buttons: {
            display: 'flex',
            width: '95%',
            margin: 'auto',
            marginTop: '2ch',
        },
        dropDownList: {
            width: '30%',
            height: '5ch',
            backgroundColor: '#D75030',
            borderStyle: 'none',
            color: '#FFFFFE',
            paddingLeft: '2ch',
            fontSize: '16px',   
            cursor: 'pointer',
            '&:focus': {
                borderStyle: 'none',
            },
        },
        dropDownListLabel: {
            color: '#FFFFFE',
            '&:focused':{
                color: '#FFFFFE',
            },
        },
        menuItem: {
            color: '#696A6B',
        },
        createTheme: {
            color: '#686A6C',
        },
}));

// LOGIQUE :

const CreatePost = props => {

    const classes = useStyles();
    const [newPostTheme, setNewPostTheme] = useState('');
    const handleChange = (event) => {
        setNewPostTheme(event.target.value);
        console.log(newPostTheme);
    };
    return(
        <div className= { classes.createPostBlock }>
            <div className={classes.createPostHeader}>
                <h2>PUBLIER UN POST</h2>
            </div>
            <div className={classes.createPostBody} >
                <div className={classes.createPostBodyContent} >
                </div>
                <div className={classes.buttons}>
                    <select className={classes.dropDownList} value={newPostTheme} placeholder="Choisissez un thème" onChange={handleChange}>
                        {props.themes && props.themes.map((theme) => { 
                        return (
                            <option key={theme.id} value={theme.id}>{theme.name}</option>
                        ); })}
                    </select>
                    <p>OU</p>
                    <input className={classes.createTheme} type="text" id="new-theme" placeholder="Saisissez un nouveau thème..." />
                </div>
            </div>
        </div>
    );

};

export default CreatePost;
