import React, { Component, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';
import { CssBaseline, Button } from '@material-ui/core';
import Post from './Post';
import axios from 'axios';

const UrlAPI = 'http://localhost:3000/api/';

// STYLE :

const PostContainer = styled.div`
    width: 80%;
    position: relative;
    paddingTop: 10ch;
`;

const ThemeContainer = styled.div`
    backgroundColor: #F1D4D4;
    width: 20%;
    height: 100%;
    position: absolute;
    position: fixed;
    right: 0; 
    top: 0;
    marginTop: 10ch;      
    paddingLeft: 1ch;
    paddingRight: 1ch;
`;

const ThemeContainerHeader = styled.div`
    backgroundColor: #D7502F;
    color: white;
    fontSize: 28px;
    fontWeight: 600;
    textAlign: center;
    padding: 1ch;
`;
const useStyles = makeStyles(() => ({
        postContainer: {
            width: '80%',
            position: 'relative',
            paddingTop: '10ch',
        },
        themeContainer: {
            backgroundColor: '#F1D4D4',
            width: '20%',
            height: '100%',
            position: 'absolute',
            position: 'fixed',
            right: '0', 
            top: '0',
            marginTop: '10ch',      
            paddingLeft: '1ch',
            paddingRight: '1ch',
        },
        themeContainerHeader: {
            backgroundColor: '#D7502F',
            color: 'white',
            fontSize: '28px',
            fontWeight: '600',
            textAlign: 'center',
            padding: '1ch',
        },
        themeButton: {
            backgroundColor: '#FFFFFE',
            color: '#696A6B',
            fontSize: '18px',
            fontWeight: '400',
            padding: '1ch',
            maxWidth: '24ch',
            width: '14ch',
            textAlign: 'center',
            margin: '1ch',
            '&:hover': {
                backgroundColor: '#D35233',
                color: 'white',
            },
        },

}));

// LOGIQUE :

class PageBodyContainer extends Component {

constructor(props) {
    super(props);
    this.state = {
        postList: null,
        themeList: null,
        classes: useStyles(),
    };
  };
//On envoi une requête GET à l'API pour récupérer un tableau 'postList' contenant des objets (1 objet / post).
getPosts = () => {
axios.get(UrlAPI + 'posts')
    .then(result => result.data)
    .then(data => this.state = {setPostList: (data)});
};
    //On envoi une requête GET à l'API pour récupérer un tableau 'themeList' contenant des objets (1 objet / theme).
    getThemes = () => {
        axios.get(UrlAPI + 'themes')
        .then(result => result.data)
        .then(data => this.state = {setThemeList: (data)});
    };

    render() {
        return (
            <>
                <CssBaseline />
                <PostContainer>
                    {this.state.postList && this.state.postList.map(post => { 
                    return <Post key={post.id} post={post} />;})}
                </PostContainer>
                <ThemeContainer >
                    <div>THÈMES</div>
                    {this.state.themeList && this.state.themeList.map(theme => { 
                        return (
                            <Button className={ this.state.classes.themeButton }> 
                                {theme && theme.name} 
                            </Button>
                        )
                    })}
                </ThemeContainer>
            </>
        );
    };
};

//<Theme filter={props.filter}  key={theme.id} theme={theme} />
export default PageBodyContainer;
