import React, { Component, useEffect } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import PostContainer from '../components/Container/PostContainer';
import ThemeContainer from '../components/Container/ThemeContainer';
import axios from 'axios';

const UrlAPI = 'http://localhost:3000/api/';

class Accueil extends Component {

  constructor(props) {
    super(props);
    this.state = { 
      themeList: null,
    };
  };

  //On envoi une requête GET à l'API pour récupérer un tableau 'themeList' contenant des objets (1 objet / theme).
    getThemes = () => {
      axios.get(UrlAPI + 'themes')
      .then(result => result.data)
      .then(data => this.setState({themeList: data}))
      .then(console.log(this.state.themeList))
      .catch(console.log('oopsy'));
    };

  handleFilter = (event) => {
    this.setState({
      themeSelected: event.target.value,
    });
    console.log(event.target.value);
  };

  render() {
    return (
      <>
        <Header />
        <PostContainer />
        <ThemeContainer filter={this.handleFilter} themeSelected={this.state.themeSelected} />
        <Footer /> 
      </>
    );
  }
};

export default Accueil;
