import React, { useState, useEffect, useRef } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import { Button } from '@material-ui/core';
import ImageUploader from 'react-images-upload';


// STYLE :

const useStyles = makeStyles((theme) => ({
    createPostBlock: {
            display: 'flex',
            flexDirection: 'column',
            width: '100ch',
            margin: 'auto',  
        },
        createPostHeader: {
            backgroundColor: '#D75030',
            color: 'white',
            width: '30ch',
            padding: '2ch',
            textAlign: 'center',
            fontSize: '12px',
        },
        createPostBody: {
            display: 'flex',
            flexDirection: 'column',
            backgroundColor: '#F2D3D4',
            padding: '2ch',
            width: '100%',
            margin: 'auto',
            marginBottom: '10ch',
        },
        createPostBodyContent: {
            backgroundColor: '#FFFFFE',
            width: '95%',
            margin: 'auto',
            padding: '2ch',
            textAlign: 'left',
        },  
        postContentInput: {
            width: '100%',
            height: '100%',
            margin: 'auto',
        },
        buttons: {
            display: 'flex',
            width: '95%',
            height: '3ch',
            margin: 'auto',
            marginTop: '2ch',
            justifyContent: 'space-between',
        },
        buttonsText: {
            color: '#D75030',
            fontWeight: '600',
            fontSize: '14px',   
            marginRight: '1ch',
        },
        dropDownList: {
            width: '30%',
            backgroundColor: '#D75030',
            borderStyle: 'none',
            color: '#FFFFFE',
            paddingLeft: '1ch',
            marginRight: '1ch',
            fontSize: '14px',   
            cursor: 'pointer',
        },
        createTheme: {
            color: '#686A6C',
            width: '35%',
            fontSize: '14px',   
            borderStyle: 'none',
            paddingLeft: '1ch',
        },
        publish: {
            backgroundColor: '#D75030',
            color: '#FFFFFE',
            borderRadius: '0',
            width: '20%',
            "&:hover": {
                backgroundColor: '#B85030',
            },
        },
}));

// LOGIQUE :

const CreatePost = props => {

    const classes = useStyles();
    const [newPostTheme, setNewPostTheme] = useState('');
    const [pictures, setPictures] = useState([]);

    const handleChange = (event) => {
        setNewPostTheme(event.target.value);
        console.log(newPostTheme);
    };

    const onDrop = (picture) => {
        setPictures(pictures.concat(picture));
    };


    //Gestion d'import d'image(s) lors de la crÃ©ation d'un post.
    const uploadedImage = useRef(null);
    const imageUploader = useRef(null);
    const handleImageUpload = e => {
        const [file] = e.target.files;
        if (file) {
            const reader = new FileReader();
            const {current} = uploadedImage;
            current.file = file;
            reader.onload = (e) => {
                current.src = e.target.result;
            }
            reader.readAsDataURL(file);
        }
    };

    return(
        <div className= { classes.createPostBlock }>
            <div className={classes.createPostHeader}>
                <h2>PUBLIER UN POST</h2>
            </div>
            <div className={classes.createPostBody} >
                <div className={classes.createPostBodyContent} >
                    <input className={classes.postContentInput} type="text" placeholder="Ecrivez le contenu de votre post ici..." />

                </div>
                <div className={classes.buttons}>
                    <select className={classes.dropDownList} value={newPostTheme} placeholder="Choisissez un thÃ¨me" onChange={handleChange}>
                        <option value="" selected data-default>Choisissez un thÃ¨me</option>
                        {props.themes && props.themes.map((theme) => { 
                        return (
                            <option key={theme.id} value={theme.id}>{theme.name}</option>
                        ); })}
                    </select>
                    <div className={classes.buttonsText}>OU</div>
                    <input className={classes.createTheme} type="text" id="new-theme" placeholder="Saisissez un nouveau thÃ¨me..." />
                    <Button className={classes.publish} >PUBLIER</Button>
                </div>
            </div>
        </div>
    );

};
